/**
 * This function takes an FTT result and bins it according to `melScale`
 * 
 * The current bining is a 16 triangle filter between 0 and 15kHz.
 * The scale are on a mel-scale, to show human frequency perception over absolute frequency
 */
void binFFT(q15_t *fftIn, uint16_t *segOut){
    q15_t binMultCurr;
    uint16_t fftIdx;
    int32_t val;
    int32_t segVal;

    for(unsigned int seg=0;seg<16;seg++){
        segVal = 0;
        
        fftIdx = fftBinMultStartIdx[seg];
        do{
            binMultCurr = fftBinMult[seg][fftIdx];

            val = fftIn[fftIdx] * binMultCurr;
            segVal += val >> 15;

            fftIdx++;
            
        }while(binMultCurr != 0);

        segVal >>= 5;
        if(segVal > 255){
            segVal = 255;
        }
        if(segVal < 0){
            segVal = 0;
        }
        segVal = linToLog[segVal];
        // segVal >>= 4;

        // segOut[15-seg] = (1 << segVal) - 1;
        segOut[15-seg] = segVal;
    }
}